import React, { useState } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  MenuItem,
  Box,
  Paper,
  CircularProgress,
} from '@mui/material';

const AIPlannerPage = () => {
  const [exam, setExam] = useState('');
  const [days, setDays] = useState('');
  const [plan, setPlan] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/plan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ exam, days }),
      });

      const data = await response.json();
      setPlan(data.plan || 'Failed to generate plan');
    } catch (error) {
      setPlan('Error generating plan');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom fontWeight="bold">
        AI Study Planner ðŸ§ 
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, maxWidth: 400 }}>
        <TextField
          label="Choose Exam"
          select
          value={exam}
          onChange={(e) => setExam(e.target.value)}
        >
          <MenuItem value="UPSC">UPSC</MenuItem>
          <MenuItem value="NEET">NEET</MenuItem>
          <MenuItem value="SSC">SSC</MenuItem>
          <MenuItem value="JEE">JEE</MenuItem>
        </TextField>
        <TextField
          label="Days left until exam"
          type="number"
          value={days}
          onChange={(e) => setDays(e.target.value)}
        />
        <Button variant="contained" onClick={handleGenerate} disabled={loading}>
          {loading ? <CircularProgress size={24} /> : 'Generate Plan'}
        </Button>
      </Box>

      {plan && (
        <Paper elevation={3} sx={{ mt: 4, p: 3, whiteSpace: 'pre-line' }}>
          <Typography variant="h6">Your AI-Generated Plan:</Typography>
          <Typography>{plan}</Typography>
        </Paper>
      )}
    </Container>
  );
};

export default AIPlannerPage;
