import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  Box,
  Grid,
} from '@mui/material';
import axios from 'axios';

const ExamPage = ({ searchTerm }) => {
  const [exams, setExams] = useState([]);
  const [newExam, setNewExam] = useState({ id: '', name: '', description: '' });

  useEffect(() => {
    fetchExams();
  }, []);

  const fetchExams = async () => {
    const res = await axios.get('http://localhost:5000/api/exams');
    setExams(res.data);
  };

  const handleAddExam = async () => {
    if (!newExam.id || !newExam.name || !newExam.description) return;
    try {
      await axios.post('http://localhost:5000/api/exams', newExam);
      setExams([...exams, newExam]);
      setNewExam({ id: '', name: '', description: '' });
    } catch (err) {
      console.error('Error adding exam:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/exams/${id}`);
      setExams(exams.filter((exam) => exam.id !== id));
    } catch (err) {
      console.error('Error deleting exam:', err);
    }
  };

  const filteredExams = exams.filter((exam) =>
    exam.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Manage Exams
      </Typography>

      <Box sx={{ display: 'flex', gap: 2, mb: 3, flexWrap: 'wrap' }}>
        <TextField
          label="ID"
          value={newExam.id}
          onChange={(e) => setNewExam({ ...newExam, id: parseInt(e.target.value) })}
        />
        <TextField
          label="Name"
          value={newExam.name}
          onChange={(e) => setNewExam({ ...newExam, name: e.target.value })}
        />
        <TextField
          label="Description"
          value={newExam.description}
          onChange={(e) => setNewExam({ ...newExam, description: e.target.value })}
        />
        <Button variant="contained" onClick={handleAddExam}>
          Add
        </Button>
      </Box>

      <Grid container spacing={2}>
        {filteredExams.map((exam) => (
          <Grid item xs={12} sm={6} md={4} key={exam.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{exam.name}</Typography>
                <Typography variant="body2">{exam.description}</Typography>
                <Button
                  variant="outlined"
                  color="error"
                  sx={{ mt: 1 }}
                  onClick={() => handleDelete(exam.id)}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default ExamPage;
