import React from 'react';
import {
  Box,
  Button,
  Container,
  Grid,
  Typography,
  Card,
  CardContent
} from '@mui/material';
import { Link } from 'react-router-dom';
import SchoolIcon from '@mui/icons-material/School';
import BarChartIcon from '@mui/icons-material/BarChart';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import Footer from '../components/Footer';

import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const HomePage = () => {
  const pieData = {
    labels: ['UPSC', 'NEET', 'JEE', 'Banking'],
    datasets: [
      {
        data: [30, 25, 20, 25],
        backgroundColor: ['#2196f3', '#4caf50', '#ff9800', '#f44336'],
        borderWidth: 1,
      },
    ],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [
      {
        label: 'Students',
        data: [1200, 1500, 1800, 2100],
        backgroundColor: '#673ab7',
      },
    ],
  };

  return (
    <>
      {/* HERO SECTION */}
      <Box
        sx={{
          background: 'linear-gradient(to right, #1e3c72, #2a5298)',
          color: 'white',
          py: { xs: 6, sm: 8 },
          textAlign: 'center',
        }}
      >
        <Container>
          <Typography variant="h3" fontWeight="bold" gutterBottom>
            Welcome to ExamsAI 🚀
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, px: { xs: 2, sm: 10 } }}>
            Your one-stop platform for mastering all competitive exams in India
          </Typography>
          <Button
            variant="contained"
            size="large"
            color="secondary"
            component={Link}
            to="/exams"
            sx={{ px: 5 }}
          >
            Explore Exams
          </Button>
        </Container>
      </Box>

      {/* FEATURE CARDS */}
      <Container sx={{ py: 8 }}>
        <Typography
          variant="h4"
          textAlign="center"
          fontWeight="bold"
          gutterBottom
        >
          Why Choose ExamsAI?
        </Typography>
        <Grid container spacing={4} sx={{ mt: 4 }}>
          {[
            {
              icon: <SchoolIcon fontSize="large" />,
              title: 'Explore Exams',
              desc: 'Get detailed info about UPSC, NEET, SSC, JEE and more.',
            },
            {
              icon: <BarChartIcon fontSize="large" />,
              title: 'Visual Stats',
              desc: 'Charts showing exam trends and popularity.',
            },
            {
              icon: <SmartToyIcon fontSize="large" />,
              title: 'AI Assistant',
              desc: 'Generate revision plans and strategies with AI.',
            },
            {
              icon: <PersonIcon fontSize="large" />,
              title: 'Add Your Own',
              desc: 'Contribute and customize your own exam list.',
            },
          ].map((feature, i) => (
            <Grid item xs={12} sm={6} md={3} key={i}>
              <Card
                elevation={4}
                sx={{ textAlign: 'center', p: 3, height: '100%' }}
              >
                <CardContent>
                  <Box sx={{ mb: 1 }}>{feature.icon}</Box>
                  <Typography variant="h6" fontWeight="bold" gutterBottom>
                    {feature.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {feature.desc}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* CHARTS SECTION */}
      <Container sx={{ py: 6 }}>
        <Typography
          variant="h4"
          textAlign="center"
          fontWeight="bold"
          gutterBottom
        >
          📊 Exam Trends
        </Typography>

        <Grid container spacing={4} alignItems="center">
          {/* Pie Chart Card */}
          <Grid item xs={12} md={6}>
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom textAlign="left">
                  Exam Distribution
                </Typography>
                <Box sx={{ maxWidth: '100%', height: '300px' }}>
                  <Pie data={pieData} />
                </Box>
              </CardContent>
            </Card>
          </Grid>

          {/* Bar Chart Card */}
          <Grid item xs={12} md={6}>
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom textAlign="left">
                  Monthly Registrations
                </Typography>
                <Box sx={{ maxWidth: '100%', height: '300px' }}>
                  <Bar
                    data={barData}
                    options={{
                      responsive: true,
                      maintainAspectRatio: false,
                    }}
                  />
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Container>

      {/* TESTIMONIALS */}
      <Box sx={{ backgroundColor: '#f5f5f5', py: 8 }}>
        <Container>
          <Typography
            variant="h4"
            textAlign="center"
            fontWeight="bold"
            gutterBottom
          >
            What Students Say 💬
          </Typography>
          <Grid container spacing={4} sx={{ mt: 3 }}>
            {[
              {
                name: 'Ravi (JEE Aspirant)',
                text: 'ExamsAI gave me all the info and stats I needed. The AI planner is a game-changer!',
              },
              {
                name: 'Meena (UPSC Candidate)',
                text: 'The exam cards and visual charts helped me prioritize my study goals!',
              },
            ].map((t, i) => (
              <Grid item xs={12} sm={6} key={i}>
                <Card elevation={3} sx={{ height: '100%' }}>
                  <CardContent>
                    <Typography variant="body1" fontStyle="italic">
                      “{t.text}”
                    </Typography>
                    <Typography
                      variant="subtitle2"
                      sx={{ mt: 2 }}
                      align="right"
                    >
                      – {t.name}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* FOOTER */}
      <Footer />
    </>
  );
};

export default HomePage;
