import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Box } from '@mui/material';

const AddExamForm = ({ onExamAdded }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description) return;

    const newExam = { id: Date.now(), name, description };

    try {
      await axios.post('http://localhost:5000/api/exams', newExam);
      setName('');
      setDescription('');
      onExamAdded(); // refresh the list
    } catch (error) {
      console.error('Error adding exam:', error);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mb: 3 }}>
      <TextField
        label="Exam Name"
        variant="outlined"
        value={name}
        onChange={(e) => setName(e.target.value)}
        sx={{ mr: 2 }}
      />
      <TextField
        label="Description"
        variant="outlined"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        sx={{ mr: 2 }}
      />
      <Button type="submit" variant="contained">Add Exam</Button>
    </Box>
  );
};

export default AddExamForm;
