import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Box,
  IconButton
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import SchoolIcon from '@mui/icons-material/School';
import AddExamForm from '../components/AddExamForm';
import ExamChart from '../charts/ExamChart';

const ExamPage = ({ searchTerm }) => {
  const [exams, setExams] = useState([]);

  // Fetch all exams from Flask backend
  const fetchExams = () => {
    axios.get('http://localhost:5000/api/exams')
      .then(res => setExams(res.data))
      .catch(err => console.error(err));
  };

  // Delete exam by ID
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/exams/${id}`);
      fetchExams(); // refresh after deletion
    } catch (err) {
      console.error('Error deleting exam:', err);
    }
  };

  useEffect(() => {
    fetchExams();
  }, []);

  // Filter exams by search input from Navbar
  const filteredExams = exams.filter((exam) =>
    exam.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container sx={{ mt: 4 }}>
      {/* Page Title with Icon */}
      <Typography
        variant="h4"
        gutterBottom
        sx={{ fontWeight: 'bold', display: 'flex', alignItems: 'center' }}
      >
        <SchoolIcon sx={{ mr: 1, fontSize: 32 }} />
        Competitive Exams Hub
      </Typography>

      {/* Add Exam Form */}
      <Box sx={{ mb: 4 }}>
        <AddExamForm onExamAdded={fetchExams} />
      </Box>

      {/* Exam Cards */}
      <Grid container spacing={2}>
        {filteredExams.map((exam) => (
          <Grid item xs={12} sm={6} md={4} key={exam.id}>
            <Card elevation={3}>
              <CardContent>
                <Box
                  sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center'
                  }}
                >
                  <Box>
                    <Typography variant="h6" sx={{ fontWeight: 600 }}>
                      {exam.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {exam.description}
                    </Typography>
                  </Box>
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(exam.id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Chart Section */}
      <Box sx={{ mt: 5 }}>
        <ExamChart />
      </Box>
    </Container>
  );
};

export default ExamPage;
