import React, { useState } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  MenuItem,
  Box,
  Paper,
  Card,
  CardContent
} from '@mui/material';

const AIPlannerPage = () => {
  const [exam, setExam] = useState('');
  const [days, setDays] = useState('');
  const [plan, setPlan] = useState('');

  const handleGenerate = () => {
    // Replace with real OpenAI fetch later
    const mockPlan = `ðŸ“š Study Plan for ${exam}:\n\n- Daily 4 hrs study\n- Focus on weak areas\n- Weekly mock tests\n- Revise daily notes`;
    setPlan(mockPlan);
  };

  return (
    <Container
      maxWidth="sm"
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        minHeight: '100vh',
        justifyContent: 'center',
        py: 6,
      }}
    >
      {/* Centered Thematic Image */}
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
        <Box
          component="img"
          src="/images/study-ai-centerpiece.jpg"
          alt="Study Planner AI"
          sx={{
            width: { xs: 120, sm: 180 },
            height: 'auto',
            borderRadius: 2,
            boxShadow: 3,
            border: '4px solid #f1f6fc',
            background: 'white',
          }}
        />
      </Box>
      {/* Stylish Card Container */}
      <Card
        elevation={6}
        sx={{
          borderRadius: 4,
          background: 'linear-gradient(135deg, #1e3c72 30%, #fccb90 100%)',
          color: '#32324b',
          p: 2,
          width: '100%',
        }}
      >
        <CardContent>
          <Typography
            variant="h4"
            sx={{
              fontWeight: 'bold',
              mb: 2,
              textAlign: 'center',
              letterSpacing: 1,
              color: '#222245',
            }}
          >
            AI Study Planner ðŸ§ 
          </Typography>
          <Box
            component="form"
            autoComplete="off"
            sx={{
              display: 'flex',
              flexDirection: 'column',
              gap: 2,
              mx: 'auto',
              maxWidth: 350,
            }}
          >
            <TextField
              label="Choose Exam"
              select
              value={exam}
              onChange={(e) => setExam(e.target.value)}
              fullWidth
              sx={{
                bgcolor: 'white',
                borderRadius: 2,
              }}
            >
              <MenuItem value="UPSC">UPSC</MenuItem>
              <MenuItem value="NEET">NEET</MenuItem>
              <MenuItem value="SSC">SSC</MenuItem>
              <MenuItem value="JEE">JEE</MenuItem>
            </TextField>
            <TextField
              label="Days left until exam"
              type="number"
              value={days}
              onChange={(e) => setDays(e.target.value)}
              fullWidth
              sx={{
                bgcolor: 'white',
                borderRadius: 2,
              }}
            />
            <Button
              variant="contained"
              size="large"
              sx={{
                mt: 1,
                bgcolor: '#1e3c72',
                fontWeight: 'bold',
                boxShadow: 2,
                borderRadius: 2,
                '&:hover': { bgcolor: '#2566be' }
              }}
              onClick={handleGenerate}
            >
              Generate Plan
            </Button>
          </Box>
        </CardContent>
      </Card>
      {/* Animated output card if plan exists */}
      {plan && (
        <Paper
          elevation={4}
          sx={{
            mt: 4,
            p: 3,
            borderRadius: 3,
            background: 'linear-gradient(90deg, #fff, #e3f1fa 80%)',
            boxShadow: 3,
            width: '100%',
            maxWidth: 480,
            whiteSpace: 'pre-line',
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}>
            Your AI-Generated Plan:
          </Typography>
          <Typography sx={{ fontFamily: 'monospace', color: '#0e315a' }}>
            {plan}
          </Typography>
        </Paper>
      )}
    </Container>
  );
};

export default AIPlannerPage;
