import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';
import AddExamForm from '../components/AddExamForm';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';

const ExamPage = () => {
  const [exams, setExams] = useState([]);

  const fetchExams = () => {
    axios.get('http://localhost:5000/api/exams')
      .then(res => setExams(res.data))
      .catch(err => console.error('Error fetching exams:', err));
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/exams/${id}`);
      fetchExams(); // refresh the list
    } catch (err) {
      console.error('Error deleting exam:', err);
    }
  };

  useEffect(() => {
    fetchExams();
  }, []);

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>List of Competitive Exams</Typography>
      <AddExamForm onExamAdded={fetchExams} />
      {exams.length === 0 ? (
        <Typography>No exams found.</Typography>
      ) : (
        <List>
          {exams.map((exam) => (
            <div key={exam.id}>
              <ListItem>
                <ListItemText primary={exam.name} secondary={exam.description} />
              </ListItem>
              <Divider />
            </div>
          ))}
        </List>
      )}
    </Container>
  );
};

export default ExamPage;
